const editExpenseHandler=async a=>{a.preventDefault();const b=document.querySelector("#plannedAmount").value.trim(),c=document.querySelector("#plannedAmount"),d=document.querySelector("#actualAmount").value.trim();if(b&&d){const a=await fetch(`/api/expense/`+c.dataset.id,{method:"PUT",body:JSON.stringify({budget_amount:b,amount_spent:d}),headers:{"Content-Type":"application/json"}});a.ok?document.location.replace("/dashboard"):location.reload()}};document.querySelector("#editBudget").addEventListener("submit",editExpenseHandler);