const loginFormHandler=async a=>{a.preventDefault();const b=document.querySelector("#email-address").value.trim(),c=document.querySelector("#password").value.trim(),d=document.querySelector(".wrong-message"),e=document.querySelector("#login-form");if(b&&c){const a=await fetch("/api/users/login",{method:"POST",body:JSON.stringify({email:b,password:c}),headers:{"Content-Type":"application/json"}});a.ok?document.location.replace("/dashboard"):(d.innerHTML="Incorrect Email or Password",e.reset())}};document.querySelector("#login-form").addEventListener("submit",loginFormHandler);