const signupFormHandler=async a=>{a.preventDefault();const b=document.querySelector("#username").value.trim(),c=document.querySelector("#email").value.trim(),d=document.querySelector("#password").value.trim();if(b&&c&&d){const a=await fetch("/api/users",{method:"POST",body:JSON.stringify({username:b,email:c,password:d}),headers:{"Content-Type":"application/json"}});a.ok?document.location.replace("/dashboard"):location.reload()}};document.querySelector("#signup-form").addEventListener("submit",signupFormHandler);